SELECT AVG("member"."mail_code" + "member"."corp_no") as agg_0, MAX("member"."middleinitial" + "member"."curr_balance") as agg_1, COUNT(*) as agg_2 FROM "member" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE ("member"."lastname" = 'WOLFMULE' OR "member"."lastname" LIKE '%JONES%') AND ("region"."state_prov" NOT LIKE '%O%N%' OR "region"."state_prov" NOT LIKE '%O%N%')