SELECT SUM("member"."member_code" + "member"."mail_code") as agg_0, MIN("member"."prev_balance" + "member"."photograph") as agg_1, SUM("provider"."country" + "member"."mail_code") as agg_2 FROM "provider" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no"  WHERE "provider"."expr_dt" LIKE '%10:4%1:24%' AND "provider"."provider_code" BETWEEN nan AND nan AND "provider"."expr_dt" LIKE '%10:41:21%' AND "member"."issue_dt" IN ('1999-10-13 10:42:54', '1999-10-13 10:41:39', '1999-10-13 10:42:35', '1999-10-13 10:43:14', '1999-10-13 10:42:08', '1999-10-13 10:41:40', '1999-10-13 10:42:40', '1999-10-13 10:43:22') AND ("charge"."catery_no" BETWEEN 27 AND 48 OR "charge"."catery_no" >= 15)