SELECT MIN("corporation"."corp_code" + "region"."phone_no") as agg_0, MAX("member"."member_no" + "member"."country") as agg_1, SUM("corporation"."country" + "corporation"."corp_no") as agg_2 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no"  WHERE ("corporation"."expr_dt" != '2004-10-12 10:41:26' OR "corporation"."corp_name" != 'Corp. Giant RestaurantsCo.' OR "corporation"."expr_dt" NOT LIKE '%10:41:26%') AND "corporation"."country" BETWEEN nan AND nan AND "member"."mail_code" BETWEEN nan AND nan