SELECT AVG("region"."phone_no" + "region"."region_code") as agg_0, MIN("region"."phone_no" + "region"."region_no") as agg_1, MAX("region"."region_code") as agg_2 FROM "region"  WHERE ("region"."country" NOT LIKE '%Ca%' OR "region"."region_name" IN ('Africa', 'South American', 'Eastern Europea', 'Scandanavian', 'Mid East / Sout', 'China')) AND "region"."street" = '777 Seventh St' AND "region"."state_prov" NOT LIKE '%O%N%' AND "region"."city" IN ('Sao Paulo', 'Beijing', 'Madras')