SELECT SUM("region"."region_no" + "provider"."phone_no") as agg_0 FROM "region" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "region"."city" LIKE '%Br%ussels%' AND "payment"."payment_dt" LIKE '%1999-0%9-02%'