SELECT MIN("charge"."charge_no") as agg_0 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no"  WHERE ("catery"."catery_code" BETWEEN nan AND nan OR "catery"."catery_no" BETWEEN 1 AND 16 OR "catery"."catery_code" BETWEEN nan AND nan) AND "catery"."catery_desc" IN ('Communication', 'Groceries', 'Meals') AND "charge"."statement_no" <= 14624 AND "catery"."catery_desc" NOT LIKE '%Communication%'