
SELECT AVG("provider"."country") as agg_0 FROM "region" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no"  WHERE "provider"."issue_dt" NOT LIKE '%1999%-10-13%' AND "corporation"."region_no" IS NOT NULL AND "member"."region_no" IS NOT NULL;