
SELECT COUNT(*) as agg_0 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no"  WHERE "category"."category_no" >= 2 AND "statement"."member_no" >= 9831 AND "charge"."category_no" >= 7 AND ("category"."category_desc" IN ('Travel', 'Misc', 'Groceries', 'Electronics', 'Home Supplies', 'Entertainment', 'Clothing', 'Meals', 'Lodging') OR "category"."category_desc" != '%Entertainment%' OR "category"."category_desc" != '%Electronics%') AND "region"."region_no" IS NOT NULL;