
SELECT SUM("member"."state_prov") as agg_0 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no"  WHERE "member"."issue_dt" NOT LIKE '%1999-10-13%' AND ("payment"."member_no" BETWEEN 3124 AND 9084 OR ("payment"."payment_no" >= 11752 AND "payment"."member_no" BETWEEN 278 AND 7498)) AND "member"."lastname" = '%YEONG%' AND "category"."category_no" IS NOT NULL;