
SELECT MIN("charge"."member_no") as agg_0 FROM "category" LEFT OUTER JOIN "charge" ON "category"."category_no" = "charge"."category_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no"  WHERE "payment"."payment_dt" LIKE '%1999-10-03%' AND "member"."firstname" != '%LQJCOXMUFETRQI%' AND "statement"."member_no" IS NOT NULL AND "corporation"."region_no" IS NOT NULL;