
SELECT SUM("corporation"."region_no") as agg_0 FROM "region" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE "region"."region_name" LIKE '%A%merican%' AND "member"."prev_balance" <= 0.0 AND "provider"."region_no" IS NOT NULL AND "charge"."member_no" IS NOT NULL AND "statement"."member_no" IS NOT NULL AND "payment"."member_no" IS NOT NULL;