
SELECT SUM("charge"."provider_no") as agg_0 FROM "region" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "payment"."member_no" >= 585 AND ("provider"."provider_name" NOT LIKE '%Ques%' OR "provider"."expr_dt" LIKE '%10:4%1:22%' OR "provider"."provider_name" LIKE '%Drive%n%') AND "payment"."payment_code" BETWEEN nan AND nan AND ("provider"."region_no" BETWEEN 7 AND 9 OR ("provider"."issue_dt" != '%1999-10-13 10:41:20%' AND "provider"."region_no" != 6)) AND "corporation"."region_no" IS NOT NULL;