
SELECT MAX("statement"."member_no") as agg_0 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "region"."region_name" IN ('Scandanavian', 'Japan', 'Western Europea', 'Africa', 'North American', 'South American') AND "statement"."statement_dt" LIKE '%1999-09-13%' AND "category"."category_no" IS NOT NULL AND "provider"."region_no" IS NOT NULL;