
SELECT AVG("charge"."charge_code") as agg_0 FROM "charge" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE ("member"."issue_dt" != '%1999-10-13 10:41:58%' OR "member"."firstname" IN ('IDS', 'NVJWQN', 'BXI', 'GOPIAYMK', 'QUHEIVWLQWGPAH') OR "member"."issue_dt" NOT LIKE '%1999-10-%13%') AND "member"."expr_dt" IN ('2000-10-12 10:41:40', '2000-10-12 10:42:40', '2000-10-12 10:43:36', '2000-10-12 10:42:54', '2000-10-12 10:43:22', '2000-10-12 10:41:39', '2000-10-12 10:42:35') AND "statement"."statement_code" BETWEEN nan AND nan AND "category"."category_no" IS NOT NULL AND "region"."region_no" IS NOT NULL AND "payment"."member_no" IS NOT NULL;