
SELECT MAX("member"."curr_balance") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "payment"."payment_dt" != '%1999-09-02 00:00:00%' AND "payment"."payment_amt" >= 1528.428165238637 AND "statement"."statement_dt" IN ('1999-08-13 00:00:00') AND ("region"."region_name" NOT LIKE '%Amer%ican%' OR "region"."state_prov" = '%ON%') AND "provider"."region_no" IS NOT NULL;