
SELECT COUNT(*) as agg_0 FROM "charge" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no"  WHERE "member"."expr_dt" LIKE '%2000-10-12%' AND "charge"."charge_no" <= 864020 AND "category"."category_desc" != '%Groceries%' AND "category"."category_no" BETWEEN 1 AND 5;