
SELECT AVG("statement"."statement_amt") as agg_0 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no"  WHERE "charge"."provider_no" <= 487 AND "provider"."issue_dt" LIKE '%10:41:23%' AND "category"."category_no" IS NOT NULL;