
SELECT AVG("corporation"."corp_code") as agg_0 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "member"."expr_dt" != '%2000-10-12 10:43:13%' AND "region"."region_no" = 3 AND "provider"."region_no" IS NOT NULL AND "charge"."member_no" IS NOT NULL;