
SELECT COUNT(*) as agg_0 FROM "region" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE ("region"."street" LIKE '%St.%' OR ("region"."region_code" BETWEEN nan AND nan AND "region"."street" NOT LIKE '%St%.%')) AND "payment"."payment_dt" NOT LIKE '%1999-09-02%' AND "charge"."provider_no" BETWEEN 460 AND 492 AND "corporation"."street" = '% %';