
SELECT COUNT(*) as agg_0 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE "region"."region_name" LIKE '%Europea%' AND "payment"."payment_dt" != '%1999-09-02 00:00:00%' AND "provider"."region_no" IS NOT NULL AND "category"."category_no" IS NOT NULL;