
SELECT AVG("corporation"."mail_code") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "provider"."expr_dt" != '%2000-10-12 10:41:20%' AND "corporation"."corp_name" != '%Corp. Famous QuestsCo.%' AND "charge"."charge_no" BETWEEN 313078 AND 1863127 AND "payment"."member_no" IS NOT NULL;