
SELECT MAX("region"."phone_no") as agg_0 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no"  WHERE "statement"."statement_dt" NOT LIKE '%1999-09-13%' AND "category"."category_desc" != '%Clothing%' AND "corporation"."region_no" IS NOT NULL;