
SELECT AVG("member"."middleinitial") as agg_0 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "member"."curr_balance" BETWEEN 0.0 AND 1.0 AND "member"."firstname" = '%NADBLT%' AND "payment"."member_no" IS NOT NULL AND "category"."category_no" IS NOT NULL AND "region"."region_no" IS NOT NULL;