
SELECT SUM("member"."mail_code") as agg_0 FROM "region" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "member"."lastname" != '%UNAKLET%' AND "corporation"."mail_code" BETWEEN nan AND nan AND "statement"."statement_dt" = '%1999-08-13 00:00:00%' AND "provider"."region_no" IS NOT NULL AND "payment"."member_no" IS NOT NULL AND "charge"."member_no" IS NOT NULL;