
SELECT AVG("member"."region_no") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "corporation"."region_no" != 1 AND "provider"."expr_dt" != '%2000-10-12 10:41:19%' AND ("provider"."provider_name" IN ('Prov. Imperial', 'Prov. Humble Sa', 'Prov. Driven Ke', 'Prov. Computer', 'Prov. Giant Mac', 'Prov. Driven Sa', 'Prov. Expert Ma') OR "provider"."provider_name" NOT LIKE '%Pe%') AND "charge"."member_no" IS NOT NULL;