
SELECT COUNT(*) as agg_0 FROM "category" LEFT OUTER JOIN "charge" ON "category"."category_no" = "charge"."category_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no"  WHERE ("member"."street" != '% %' OR "member"."expr_dt" LIKE '%2000-10-12%') AND "category"."category_no" BETWEEN 1 AND 2 AND ("statement"."statement_dt" != '%1999-08-13 00:00:00%' OR ("statement"."due_dt" LIKE '%199%9-09-02%' AND "statement"."statement_dt" != '%1999-09-13 00:00:00%')) AND "region"."region_code" BETWEEN nan AND nan AND "corporation"."region_no" IS NOT NULL;