
SELECT AVG("member"."curr_balance") as agg_0 FROM "region" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no"  WHERE "statement"."statement_dt" IN ('1999-08-13 00:00:00') AND ("corporation"."corp_name" IN ('Corp. Famous Sail MakersInc.', 'Corp. Humble NeedleworkCorp.', 'Corp. Famous QuestsCo.', 'Corp. Computer QuestsInc.', 'Corp. Humble OperaAg.', 'Corp. Expert Sail MakersCo.', 'Corp. Boston MachineryLtd.', 'Corp. Giant RestaurantsCorp.') OR ("corporation"."corp_name" NOT LIKE '%MakersA%g.%' AND "corporation"."corp_name" = '%Corp. Driven Sail MakersCo.%')) AND "payment"."payment_dt" LIKE '%1999-%09-02%' AND "charge"."member_no" IS NOT NULL AND "provider"."region_no" IS NOT NULL;