
SELECT MAX("region"."phone_no") as agg_0 FROM "category" LEFT OUTER JOIN "charge" ON "category"."category_no" = "charge"."category_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE ("category"."category_no" BETWEEN 2 AND 7 OR "category"."category_desc" = '%Clothing%') AND "statement"."due_dt" = '%1999-10-03 00:00:00%' AND "provider"."city" = '% %' AND "corporation"."region_no" IS NOT NULL;