
SELECT AVG("payment"."statement_no") as agg_0 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "category" ON "charge"."category_no" = "category"."category_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "region"."city" IN ('Madras', 'Beijing', 'Goteborg') AND "statement"."due_dt" IN ('1999-09-02 00:00:00') AND "category"."category_no" IS NOT NULL;