
SELECT SUM("statement"."statement_amt") as agg_0 FROM "region" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "statement"."member_no" <= 1759 AND ("provider"."street" = '% %' OR "provider"."country" BETWEEN nan AND nan) AND "member"."expr_dt" NOT LIKE '%20%00-10-12%' AND "corporation"."region_no" IS NOT NULL AND "payment"."member_no" IS NOT NULL AND "charge"."member_no" IS NOT NULL;