
SELECT MAX("statement"."statement_no") as agg_0 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "charge"."provider_no" >= 490 AND "charge"."charge_no" >= 1216541 AND ("statement"."statement_no" >= 7857 OR ("statement"."statement_dt" != '%1999-08-13 00:00:00%' AND "statement"."statement_no" BETWEEN 16030 AND 17643)) AND "provider"."region_no" IS NOT NULL;