
SELECT SUM("member"."prev_balance") as agg_0 FROM "region" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no"  WHERE "region"."region_code" BETWEEN nan AND nan AND "payment"."member_no" >= 5645 AND "payment"."statement_no" >= 0 AND ("payment"."payment_dt" LIKE '%1999%-10-03%' OR ("payment"."payment_dt" NOT LIKE '%199%9-10-03%' AND "payment"."payment_dt" LIKE '%00:00:00%')) AND "charge"."member_no" IS NOT NULL AND "corporation"."region_no" IS NOT NULL;