SELECT SUM("district"."A10") as agg_0 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id"  WHERE "district"."A2" NOT LIKE '%Beroun%' AND ("account"."date" != '1995-10-20' OR "account"."account_id" <= 58643) AND "disp"."account_id" <= 10998 AND "district"."A13" >= 3.6016853210938162 AND ("disp"."type" != 'OWNER' OR "disp"."client_id" BETWEEN 72393 AND 135366) AND "client"."gender" = 'M'