SELECT SUM("card"."card_id" + "district"."A4") as agg_0 FROM "account" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "card"."type" NOT LIKE '%c%lassic%' AND "district"."A3" LIKE '%central%' AND "disp"."account_id" BETWEEN 8995 AND 96130