SELECT COUNT(*) as agg_0, SUM("district"."district_id" + "district"."A10") as agg_1 FROM "district" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "disp"."client_id" <= 30735 AND "card"."disp_id" <= 28208