SELECT MAX("trans"."account" + "loan"."payments") as agg_0 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id"  WHERE "account"."frequency" NOT LIKE '%P%O%' AND "trans"."type" IN ('PRIJEM') AND "account"."date" != '1996-06-26' AND "account"."frequency" IN ('POPLATEK PO OBRATU', 'POPLATEK TYDNE')