SELECT MIN("district"."A16" + "district"."A11") as agg_0, AVG("district"."A9" + "district"."A8") as agg_1 FROM "district"  WHERE ("district"."A16" != 5566 OR "district"."A10" BETWEEN 52.908849525562054 AND 53.39355784690368 OR "district"."A16" BETWEEN 2030 AND 2517) AND "district"."A8" <= 4 AND "district"."A3" IN ('east Bohemia', 'north Moravia', 'north Bohemia', 'south Bohemia') AND "district"."A2" IN ('Svitavy', 'Breclav', 'Blansko', 'Trutnov', 'Hl.m. Praha', 'Semily', 'Brno - mesto', 'Brno - venkov')