SELECT SUM("loan"."loan_id" + "district"."A14") as agg_0, SUM("loan"."payments") as agg_1 FROM "loan" LEFT OUTER JOIN "account" ON "loan"."account_id" = "account"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "account"."frequency" != 'POPLATEK TYDNE' AND "loan"."status" IN ('A')