SELECT MAX("trans"."balance") as agg_0 FROM "account" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id"  WHERE ("account"."frequency" IN ('POPLATEK TYDNE', 'POPLATEK MESICNE') OR ("account"."frequency" LIKE '%OB%RATU%' AND "account"."frequency" LIKE '%PO%')) AND "disp"."type" IN ('OWNER') AND "disp"."disp_id" <= 41710 AND ("trans"."date" != '1997-03-30' OR "trans"."balance" >= 38036 OR "trans"."date" IN ('1998-11-30', '1998-09-30', '1998-07-31', '1998-02-28', '1998-03-31', '1998-08-31'))