SELECT MAX("district"."A12" + "client"."district_id") as agg_0, MIN("account"."account_id" + "district"."A8") as agg_1, MIN("client"."client_id" + "district"."A5") as agg_2 FROM "district" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "account"."frequency" = 'POPLATEK PO OBRATU' AND "client"."gender" = 'M' AND "client"."gender" IN ('M') AND "district"."A3" NOT LIKE '%Mor%avia%' AND "district"."district_id" <= 29