SELECT SUM("district"."A16") as agg_0, AVG("client"."district_id") as agg_1, MIN("district"."A4") as agg_2 FROM "disp" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id"  WHERE "account"."frequency" = 'POPLATEK PO OBRATU' AND "client"."client_id" <= 98460 AND "disp"."account_id" >= 89553