SELECT SUM("card"."card_id") as agg_0, MAX("disp"."account_id") as agg_1 FROM "client" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id"  WHERE "district"."A12" >= 1.7335892782695668 AND "account"."account_id" <= 2316 AND "card"."type" LIKE '%ld%' AND "client"."gender" IN ('F') AND "client"."district_id" BETWEEN 261 AND 621