SELECT SUM("district"."A5" + "district"."A7") as agg_0, MAX("client"."district_id") as agg_1, AVG("district"."A4") as agg_2 FROM "client" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id"  WHERE "disp"."type" NOT LIKE '%DISP%ONENT%' AND "disp"."type" IN ('DISPONENT') AND "district"."A2" LIKE '%Var%y%'