SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id"  WHERE "client"."district_id" >= 695 AND ("district"."A2" NOT LIKE '%Mlada%' OR "district"."A7" >= 13) AND "disp"."type" IN ('OWNER')