SELECT AVG("disp"."client_id") as agg_0 FROM "district" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id"  WHERE "card"."issued" = '1997-07-30' AND "disp"."type" LIKE '%DISPONENT%' AND "district"."A3" LIKE '%Mora%via%'