SELECT AVG("account"."district_id" + "district"."A13") as agg_0, MIN("district"."A11" + "district"."A9") as agg_1 FROM "account" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "trans"."type" NOT LIKE '%VY%DAJ%'