SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, SUM("district"."A7" + "district"."A4") as agg_2 FROM "district" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "district"."A3" LIKE '%south%' AND "account"."frequency" IN ('POPLATEK MESICNE') AND "trans"."k_symbol" IS NOT NULL AND "account"."frequency" != 'POPLATEK PO OBRATU' AND "trans"."operation" IS NOT NULL