SELECT MIN("district"."A6" + "district"."A14") as agg_0, COUNT(*) as agg_1, SUM("client"."client_id" + "district"."A5") as agg_2 FROM "district" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id"  WHERE "district"."A11" = 9553