SELECT MIN("account"."account_id") as agg_0 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id"  WHERE "card"."issued" = '1998-12-29' AND "account"."frequency" LIKE '%OBRATU%' AND "card"."type" NOT LIKE '%c%lassic%' AND "disp"."type" != 'OWNER'