SELECT MIN("disp"."disp_id" + "disp"."client_id") as agg_0, COUNT(*) as agg_1, MAX("card"."disp_id") as agg_2 FROM "disp" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "disp"."disp_id" <= 44351 AND "card"."issued" = '1994-07-21' AND "card"."type" IN ('ld', 'classic') AND "disp"."type" NOT LIKE '%D%ISPONENT%' AND "card"."card_id" BETWEEN 556 AND 575 AND "disp"."client_id" BETWEEN 3584 AND 103498